cards.style:
.card.card1 {
  width: auto;
  right: 50px;
  box-shadow: none;
}

.neon-table {
  border-collapse: collapse;
  margin: 2em auto 4em;
  position: relative;
  z-index: 0;
}
.neon-table td {
  min-width: 3em;
  height: 3em;
  text-align: center;
  padding: 1em 1.5em;
  border: 1px solid #ccc;
  border-top-width: 0;
}
.neon-table td span {
  display: block;
  text-align: center;
  margin: 0 auto;
}
.neon-table td .text {
  font: 1.5em 'Open Sans', sans-serif;
  border-radius: 1em;
  padding: 0 0.3em;
}
.neon-table td .text.ok {
  background: #f2fcf3;
  border: 1px solid #77d375;
  color: #1b7b19;
}
.neon-table td .text.warn {
  background: #fcf9f2;
  border: 1px solid #ffc368;
  color: #bb7408;
}
.neon-table td .text.alert {
  background: #fcf5f6;
  border: 1px solid #ff7060;
  color: #ca3f30;
}
.neon-table td .circle {
  display: block;
  width: 1em;
  height: 1em;
  border-radius: 50%;
}
.neon-table td .circle.unknown {
  border: 2px solid #aeaeae;
}
.neon-table td .circle.ok {
  background: #77d375;
  box-shadow: 0 0 0 0.75em rgba(119, 211, 117, 0.1);
}
.neon-table td .circle.alert {
  background: #ff7060;
  animation: pulse 1s infinite;
}
@keyframes pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(255, 112, 96, 0.3);
  }
  100% {
    box-shadow: 0 0 0 2em rgba(255, 112, 96, 0);
  }
}
.neon-table th {
  background-color: transparent;
}
.neon-table th.rotate {
  height: 8em;
  white-space: nowrap;
  overflow: visible;
  font: 1.5em/2.25em 'Open Sans', sans-serif;
  text-transform: uppercase;
}
.neon-table th.rotate > div {
  -webkit-transform: translate(25px, 51px) rotate(315deg);
  transform: translate(25px, 51px) rotate(315deg);
  width: 3em;
}
.neon-table th.rotate:nth-child(2) span:before {
  border-top: 1px solid lightgrey;
}
.neon-table th.rotate span {
  display: inline-block;
  border-bottom: 1px solid lightgrey;
  height: 50px;
  text-indent: 1px;
  position: relative;
  top: 7px;
  min-width: 210px;
  background-color: #edeeee;
  text-align: left;
  padding-left: 10px;
}
.neon-table th.rotate span:before {
  content: '';
  display: inline-block;
  width: 100%;
  height: 100%;
  position: absolute;
  left: -55px;
  top: -1px;
  background-color: #edeeee;
  z-index: -9;
}
.neon-table th.rotate span:after {
  content: '';
  display: inline-block;
  width: 100%;
  height: 74%;
  position: absolute;
  left: 100px;
  background-color: #fff;
  z-index: 1;
  transform: rotate(45deg);
}
.neon-table th.row-header {
  padding: 0 1em;
  border-bottom: 1px solid #ccc;
  font: 1.5em/2.25em 'Open Sans', sans-serif;
  text-transform: uppercase;
  background-color: #edeeee;
  position: relative;
}
.neon-table th.row-header:first-child {
  border-top: 1px solid lightgrey;
}

.neon-table tbody tr:first-child td {
  box-shadow: inset 0 1px 0 lightgrey;
}

.neon-table tbody tr {
  position: relative;
  z-index: 1000;
}

.neon-table tbody td {
  position: relative;
  z-index: 1000;
  background-color: #ffffff;
}

.neon-table tr:nth-child(2n) > td {
  background-color: #ffffff;
}

.neon-table tbody {
  box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.025);
}

.card.card2 {
  position: fixed;
  background: transparent;
  box-shadow: none;
  border: 2px solid #111;
  left: auto !important;
  top: auto !important;
  right: 10%;
  bottom: 10%;
  max-width: 30%;
  background: linear-gradient(90deg, #fff 9px, transparent 1%) center, linear-gradient(#fff 9px, transparent 1%) center, #ccc;
  background-size: 10px 10px;
  box-shadow: 1px 1px #ccc, 2px 2px #ccc, 3px 3px #ccc;
  padding: 0;
  z-index: 901 !important;
}

.card2 .title {
  font: 2em 'Caveat', cursive;
  padding: .3em .3em .1em;
  border-bottom: 2px solid #111;
}

.card2 .content {
  font-family: Menlo, Consolas, monospace;
  padding: .5em;
  line-height: 1.2;
}

.card2 .content p, .card2 .content pre {
  margin-bottom: 1em;
}

.card2 .content pre {
  background-color: #f2f2f2;
  color: #ef5777;
  white-space: normal;
  padding: .25em .5em;
  line-height: 1.25em;
  border-radius: .25em;
}

.card2 .content p code {
  background-color: #f2f2f2;
  border-radius: .25em;
  padding: 0 0.2em;
  vertical-align: baseline;
  color: #ef5777;
}

.card2 .content pre .dot {
  color: #8a7bff;
}

.card2 .content a {
  color: #3867d6;
}

