cards.style:
.card.card2 {
  position: fixed;
  background: transparent;
  box-shadow: none;
  border: 2px solid #111;
  left: auto !important;
  top: auto !important;
  right: 10%;
  bottom: 10%;
  max-width: 30%;
  background: linear-gradient(90deg, #fff 9px, transparent 1%) center, linear-gradient(#fff 9px, transparent 1%) center, #ccc;
  background-size: 10px 10px;
  box-shadow: 1px 1px #ccc, 2px 2px #ccc, 3px 3px #ccc;
  padding: 0;
}

.card2 .title {
  font: 2em 'Caveat', cursive;
  padding: .3em .3em .1em;
  border-bottom: 2px solid #111;
}

.card2 .content {
  font-family: Menlo, Consolas, monospace;
  padding: .5em;
  line-height: 1.2;
}

.card2 .content p, .card2 .content pre {
  margin-bottom: 1em;
}

.card2 .content pre {
  background-color: #f2f2f2;
  color: #ef5777;
  white-space: normal;
  padding: .25em .5em;
  line-height: 1.25em;
  border-radius: .25em;
}

.card2 .content p code {
  background-color: #f2f2f2;
  border-radius: .25em;
  padding: 0 0.2em;
  vertical-align: baseline;
  color: #ef5777;
}

.card2 .content pre .dot {
  color: #8a7bff;
}

.card2 .content a {
  color: #3867d6;
}
