cards.style:
.card.card1 {
  background-color: transparent;
  box-shadow: none !important;
}

.card1 .board {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  padding: 5px 0;
  height: 200px;
  width: 70px;
  background: radial-gradient(#1D1F20 0%, #333333 100%);
  box-shadow: 0 5px 15px 0px rgba(0,0,0,0.6);
  border-radius: 50px;
}

.card1 .light {
  border-radius: 100%;
  position: relative;
  height: 40px;
  width: 40px;
}

.red{
  background: red;
  background-image: radial-gradient(brown, transparent);
  background-size: 3px 3px; 
  box-shadow: 0 0 20px #111 inset, 0 0 10px red;
  opacity: .1;
}

.yellow{
  background: yellow;
  background-image: radial-gradient(orange, transparent);
  background-size: 3px 3px;
  box-shadow: 0 0 20px #111 inset, 0 0 10px yellow;
  opacity: .1;
}

.green{
  background: green;
  background-image: radial-gradient(lime, transparent);
  background-size: 3px 3px;
  box-shadow: 0 0 20px #111 inset, 0 0 10px lime;
  opacity: .1;
}

.on {
  animation: turnon 1s;
  animation-fill-mode: forwards;
}

@keyframes turnon {
  to {opacity: 1}
}

.card.card2 {
  background-color: transparent;
  box-shadow: none !important;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  position: relative;
  left: 70px !important;
  top: 300px !important;
  background-color: {{? query1.val < 3}}#fc625d{{?? query1.val < 6}}#fdbe40{{??}}#34cb4a{{?}};
  transition: background-color 1s;
}

.card2 .counter {
  font-size: 0;
  height: 30px;
  overflow: hidden;
  font-family: Helvetica Neue;
  font-weight: bold;
  text-align: center;
}
.card2 .counter .digit {
  display: inline-block;
  font-size: 24px;
  text-align: center;
  color: #fff;
  text-shadow: .03em .03em 0 hsla(230,40%,50%,1);
}
.card2 .counter .sequence {
  width: 0.55em;
  transform: translate3d(0, -90%, 0);
  transition: transform 400ms cubic-bezier(0.86, 0, 0.07, 1),
              width 400ms ease-in-out,
              opacity 400ms ease-in-out;
}

.card2 .counter .digit:last-child .sequence{
  transition-delay: 100ms;
}

.card .counter .sequence.is-hidden {
  width: 0.02em;
  opacity: 0;
}

.card .counter .sequence.anim {
  transform: translate3d(0, -{{=(9 -query1.val) * 10}}%, 0);
}

@import url('https://fonts.googleapis.com/css2?family=Caveat&family=Space+Mono&display=swap');
.card.card3 {
  position: static;
  background: transparent;
  box-shadow: none;
}

.card3 .notes {
  position: absolute;
  border: 2px solid #111;
  right: 10%;
  bottom: 10%;
  max-width: 30%;
  background: linear-gradient(90deg, #fff 9px, transparent 1%) center, linear-gradient(#fff 9px, transparent 1%) center, #ccc;
  background-size: 10px 10px;
  box-shadow: 1px 1px #ccc, 2px 2px #ccc, 3px 3px #ccc;
}

.card3 .title {
  font: 2em 'Caveat', cursive;
  padding: .3em .3em .1em;
  border-bottom: 2px solid #111;
}

.card3 .content {
  font-family: 'Space Mono', monospace;
  padding: .5em;
  line-height: 1.2;
}

.card3 .content p, .card3 .content pre {
  margin-bottom: 1em;
}

.card3 .content pre {
  background-color: #f2f2f2;
  color: #ef5777;
  white-space: normal;
  padding: .25em .5em;
  line-height: 1.25em;
  border-radius: .25em;
}

.card3 .content p code {
  background-color: #f2f2f2;
  border-radius: .25em;
  padding: 0 0.2em;
  vertical-align: baseline;
  color: #ef5777;
}

.card3 .content pre .dot {
  color: #8a7bff;
}
